changeSet.sql:
CREATE TABLE client ( id UUID PRIMARY KEY, first_name VARCHAR NOT NULL, last_name VARCHAR NOT NULL, balance INTEGER NOT NULL );
CREATE TABLE contract ( id UUID PRIMARY KEY, client_id UUID NOT NULL, status VARCHAR NOT NULL, FOREIGN KEY (client_id) REFERENCES client (id) ON DELETE CASCADE );
CREATE TABLE internet ( client_id UUID NOT NULL, internet_title VARCHAR NOT NULL, internet_cost INTEGER NOT NULL, FOREIGN KEY (client_id) REFERENCES client (id) ON DELETE CASCADE ); CREATE TABLE mobile ( client_id UUID NOT NULL, title VARCHAR NOT NULL, cost INTEGER NOT NULL, FOREIGN KEY (client_id) REFERENCES client (id) ON DELETE CASCADE ); CREATE TABLE penalty ( client_id UUID NOT NULL, title VARCHAR NOT NULL, cost INTEGER NOT NULL, FOREIGN KEY (client_id) REFERENCES client (id) ON DELETE CASCADE );
